executeFunc((function productReviewForm(){const rates=document.querySelectorAll(".rate");rates.forEach((rate=>{const checks=Array.from(rate.querySelectorAll(".form-check"));let $selected=-1;function highlight(index,className){checks.forEach(((check,i)=>{const label=check.querySelector("label");if(i<=index){label.classList.add(className)}else{label.classList.remove(className)}}))}function clearHover(){checks.forEach((check=>check.querySelector("label").classList.remove("hovered")))}function clearActive(){checks.forEach((check=>check.querySelector("label").classList.remove("active")))}checks.forEach(((check,i)=>{const label=check.querySelector("label");label.addEventListener("mouseenter",(()=>highlight(i,"hovered")));label.addEventListener("mouseleave",clearHover);label.addEventListener("click",(()=>{$selected=i;clearActive();highlight(i,"active")}));check.querySelector("input").addEventListener("focus",(()=>highlight(i,"hovered")));check.querySelector("input").addEventListener("blur",clearHover);check.querySelector("input").addEventListener("change",(()=>{$selected=i;clearActive();highlight(i,"active")}))}));const checked=rate.querySelector("input:checked");if(checked){const idx=checks.findIndex((check=>check.querySelector("input")===checked));if(idx!==-1){highlight(idx,"active");$selected=idx}}}));return true}));